import { useSelector, useDispatch } from "react-redux/es/exports";
import { decreaseCart,addToCart,getTotals } from "./cartSlice";
import { useEffect } from "react";

export default function Cart(){
    const cart = useSelector((state) => state.cart);
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getTotals());
      }, [cart, dispatch]);

    const handelDecreaseCart = (cartItem) =>{
        dispatch(decreaseCart(cartItem));
    }
    const handleIncreaseCart = (product) => {
        dispatch(addToCart(product));
      };

    return(
        <section className=" cart-container aem-Grid aem-Grid--12">
            <h2>Your Shopping Bag</h2>
            { cart.cartItems.length === 0 ? (
                <div className="cart-empty">
                    <p>Your Cart is currently Empty</p>
                    <div className="start-shopping">
                        <a href="#/home" aria-label="home">
                            Start Shopping
                        </a>
                    </div>
                </div>
            ) : (
                <>
                <div className="aem-GridColumn aem-GridColumn--default--8 aem-GridColumn--phone--12 aem-GridColumn--tablet--12">
                    <div>
                        {cart.cartItems?.map(cartItem => (
                            <div key={cartItem.id} className="cart-container">
                                <img src={cartItem.image} alt={cartItem.title}/>
                                <h4>{cartItem.title}</h4>
                                <div className="quantity">
                                    <button type="button" onClick={() => handelDecreaseCart(cartItem)}>-</button>
                                    <input type="text" value={cartItem.cartQuantity}  />
                                    <button type="button" onClick={() => handleIncreaseCart(cartItem)}>+</button>
                                </div>
                                <span>Price: ${cartItem.price * cartItem.cartQuantity}</span>
                            </div>
                        ))}
                    </div>
                </div>
                <div className="aem-GridColumn aem-GridColumn--default--4 aem-GridColumn--phone--12 aem-GridColumn--tablet--12">
                    <div className="pricing-summary">
                            <h4>Pricing Summary</h4>
                            <span>Subtotal: ${cart.cartTotalAmount}</span>
                            <span>Coupon: $0</span>
                            <span>Gift Card: $0</span>
                            <span>Estimated Tax: $0</span>
                            <span>Estimated Shipping: $0</span>
                            <span>Estimated Total: ${cart.cartTotalAmount}</span>
                            <button>Checkout</button>
                    </div>
                </div>
                </>
            )}
        </section>
    );
}
