import React,{Suspense,lazy} from "react";
import {Route,HashRouter,Routes} from 'react-router-dom';
import fav from "../../Images/heart.png";
import Banner from "../Banner/Banner";
import Sidebar from "../Sidebar/Sidebar";
import SingleProduct from "../SingleProduct/SingleProduct";
export default function ProductCards(props){
    return(
          <>
          <Banner/>
          <section className="aem-Grid aem-Grid--12 products-grid">
            <div className="aem-GridColumn aem-GridColumn--default--3 aem-GridColumn--phone--hide aem-GridColumn--tablet--hide">
              <Sidebar/>
            </div>
            <div className="aem-GridColumn aem-GridColumn--default--9 aem-GridColumn--phone--12 aem-GridColumn--tablet--12 aem-Grid aem-Grid--12">
              <div className="aem-GridColumn aem-GridColumn--default--6">
                <span className="result-count">38 Results</span>
              </div>
              <div className="aem-GridColumn aem-GridColumn--default--6">
                <select className="sorting">
                  <option>Sort by Latest</option>
                  <option>Price: High to Low</option>
                  <option>Price: Low to High</option>
                </select>
              </div>
              {props.data.map((product) => {
                return (
                  <>
                  <section key={product.id} className="prod-items aem-GridColumn aem-GridColumn--default--4 aem-GridColumn--tablet--4 aem-GridColumn--phone--10">
                    <div className="prod-image">
                      <img src={product.image} alt={product.title}/>
                    </div>
                    <HashRouter>
                      <a href='#/product' className="prod-title">{product.title}</a>
                      <Route exact path="/product" component={() => <SingleProduct data={product.id} />} />
                    </HashRouter>
                    <span className="prod-price">${product.price}</span>
                    <img className="fav-prod" src={fav} alt="add favorite"/>
                  </section>    
                </>
                );
              })}
            </div>
            </section>
            </>
          );
}
